import React from 'react';

const LiveGamesBar = ({ games }) => {
  if (!games || games.length === 0) {
    return (
      <div className="card p-4">
        <div className="flex items-center justify-center text-gray-500">
          <span>No live games today</span>
        </div>
      </div>
    );
  }

  return (
    <div className="card p-4">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-semibold text-gray-900 flex items-center">
          <span className="live-indicator mr-2"></span>
          Live Games
        </h2>
        <span className="text-sm text-gray-500">
          {games.length} game{games.length !== 1 ? 's' : ''} today
        </span>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        {games.map((game, index) => (
          <div key={index} className="bg-gradient-to-br from-gray-50 to-gray-100 rounded-lg p-4 border border-gray-200 hover:shadow-md transition-shadow duration-200">
            <div className="text-center">
              {/* Teams */}
              <div className="mb-3">
                <div className="text-sm font-medium text-gray-700 mb-1">
                  {game.away_team}
                </div>
                <div className="text-xs text-gray-500 mb-1">@</div>
                <div className="text-sm font-medium text-gray-700">
                  {game.home_team}
                </div>
              </div>

              {/* Score */}
              <div className="mb-3">
                <div className="text-2xl font-bold text-gray-900">
                  {game.away_score} - {game.home_score}
                </div>
              </div>

              {/* Game Status */}
              <div className="text-xs text-gray-600 bg-gray-200 rounded-full px-2 py-1">
                {game.game_status}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LiveGamesBar;
