import React from 'react';

const LiveGamesBar = ({ games }) => {
  if (!games || games.length === 0) {
    return (
      <div className="px-6 py-1 bg-white border-b border-gray-200">
        <div className="flex items-center justify-center text-gray-500">
          <span className="text-xs">No live games today</span>
        </div>
      </div>
    );
  }

  return (
    <div className="px-6 py-1 bg-white border-b border-gray-200">
      <div className="flex items-center justify-between mb-1">
        <h2 className="text-sm font-semibold text-gray-900 flex items-center">
          <span className="live-indicator mr-2"></span>
          Live Games
        </h2>
        <span className="text-xs text-gray-500">
          {games.length} game{games.length !== 1 ? 's' : ''} today
        </span>
      </div>

      <div className="flex overflow-x-auto space-x-3 pb-0.5">
        {games.map((game, index) => (
          <div key={index} className="flex-shrink-0 bg-gradient-to-br from-gray-50 to-gray-100 rounded-lg px-2 py-1 border border-gray-200 hover:shadow-md transition-shadow duration-200 min-w-[160px]">
            <div className="flex items-center justify-between">
              {/* Teams and Score */}
              <div className="flex items-center space-x-2">
                <div className="text-center">
                  <div className="text-xs font-medium text-gray-700">
                    {game.away_team}
                  </div>
                  <div className="text-sm font-bold text-gray-900">
                    {game.away_score}
                  </div>
                </div>

                <div className="text-xs text-gray-500">@</div>

                <div className="text-center">
                  <div className="text-xs font-medium text-gray-700">
                    {game.home_team}
                  </div>
                  <div className="text-sm font-bold text-gray-900">
                    {game.home_score}
                  </div>
                </div>
              </div>

              {/* Game Status */}
              <div className="text-xs text-gray-600 bg-gray-200 rounded-full px-1.5 py-0.5 ml-2">
                {game.game_status}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LiveGamesBar;
