import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = ({ teams }) => {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const location = useLocation();

  // Auto-collapse on mobile
  const handleCollapseToggle = () => {
    setIsCollapsed(!isCollapsed);
  };

  const navigationItems = [
    {
      name: 'Dashboard',
      path: '/',
      icon: '🏠'
    },
    {
      name: 'Live Games',
      path: '/games',
      icon: '🏀'
    },
    {
      name: 'Standings',
      path: '/standings',
      icon: '📊'
    },
    {
      name: 'Analytics',
      path: '/analytics',
      icon: '📈'
    },
    {
      name: 'Predictions',
      path: '/predictions',
      icon: '🔮'
    }
  ];

  return (
    <div className={`h-full bg-white shadow-lg border-r border-gray-200 transition-all duration-300 ${
      isCollapsed ? 'w-16' : 'w-64'
    }`}>
      {/* Sidebar Header */}
      <div className="flex items-center justify-between p-4 border-b border-gray-200">
        {!isCollapsed && (
          <div className="flex items-center">
            <div className="w-8 h-8 bg-gradient-to-br from-nba-blue to-nba-red rounded-full flex items-center justify-center text-white text-sm font-bold mr-2">
              NBA
            </div>
            <span className="text-lg font-bold text-gray-900">Analytics Pro</span>
          </div>
        )}
        <button
          onClick={() => setIsCollapsed(!isCollapsed)}
          className="p-2 rounded-lg hover:bg-gray-100 transition-colors"
        >
          <svg 
            className={`w-5 h-5 text-gray-600 transition-transform duration-300 ${isCollapsed ? 'rotate-180' : ''}`} 
            fill="none" 
            stroke="currentColor" 
            viewBox="0 0 24 24"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" />
          </svg>
        </button>
      </div>

      {/* Navigation Menu */}
      <nav className="p-4 space-y-2">
        {navigationItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`flex items-center p-3 rounded-lg transition-all duration-200 group ${
              location.pathname === item.path
                ? 'bg-nba-blue text-white shadow-md'
                : 'hover:bg-gray-100 text-gray-700'
            }`}
          >
            <span className="text-lg mr-3">{item.icon}</span>
            {!isCollapsed && (
              <span className="font-medium">{item.name}</span>
            )}
          </Link>
        ))}
      </nav>

      {/* Teams Section */}
      <div className="border-t border-gray-200 mt-4">
        <div className="p-4">
          <div className="flex items-center justify-between mb-3">
            {!isCollapsed && (
              <h3 className="text-sm font-semibold text-gray-900 uppercase tracking-wide">
                Teams
              </h3>
            )}
            <span className="text-xs text-gray-500">
              {isCollapsed ? teams.length : `${teams.length} teams`}
            </span>
          </div>

          {/* Teams List */}
          <div className={`overflow-y-auto ${isCollapsed ? 'max-h-80' : 'max-h-96'}`}>
            <div className="space-y-2">
              {teams.map((team, index) => (
                <Link
                  key={team.id || index}
                  to={`/team/${team.id}`}
                  className="block group"
                  title={isCollapsed ? team.full_name : ''}
                >
                  <div className={`flex items-center p-2 rounded-lg hover:bg-gray-100 transition-all duration-200 ${
                    isCollapsed ? 'justify-center' : ''
                  }`}>
                    {/* Team Logo/Avatar */}
                    <div className="w-8 h-8 bg-gradient-to-br from-nba-blue to-nba-red rounded-full flex items-center justify-center text-white text-xs font-bold group-hover:scale-105 transition-transform duration-200">
                      {team.abbreviation || team.full_name?.substring(0, 3).toUpperCase() || 'NBA'}
                    </div>

                    {/* Team Info - Hidden when collapsed */}
                    {!isCollapsed && (
                      <div className="flex-1 ml-3">
                        <div className="text-sm font-medium text-gray-900 group-hover:text-nba-blue transition-colors truncate">
                          {team.full_name || team.city + ' ' + team.nickname || `Team ${index + 1}`}
                        </div>
                        <div className="text-xs text-gray-500 truncate">
                          {team.city || 'NBA Team'}
                        </div>
                      </div>
                    )}
                  </div>
                </Link>
              ))}
            </div>
          </div>

          {/* Scroll indicator - only show when not collapsed */}
          {!isCollapsed && teams.length > 10 && (
            <div className="mt-3 flex justify-center">
              <div className="text-xs text-gray-400 flex items-center">
                <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
                </svg>
                Scroll for more
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Footer - only show when not collapsed */}
      {!isCollapsed && (
        <div className="absolute bottom-0 left-0 right-0 p-4 border-t border-gray-200 bg-gray-50">
          <div className="text-xs text-gray-500 text-center">
            NBA Analytics Pro v1.0
          </div>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
