import React from 'react';
import { Link } from 'react-router-dom';

const HorizontalTeamsBar = ({ teams }) => {
  return (
    <div className="card p-4">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-lg font-semibold text-gray-900 flex items-center">
          <span className="mr-2">üèÄ</span>
          NBA Teams
        </h2>
        <span className="text-sm text-gray-500">
          {teams.length} teams
        </span>
      </div>

      {/* Horizontal scrollable teams container */}
      <div className="overflow-x-auto">
        <div className="flex space-x-3 pb-2" style={{ minWidth: 'max-content' }}>
          {teams.map((team, index) => (
            <Link
              key={team.id || index}
              to={`/team/${team.id}`}
              className="flex-shrink-0 group"
            >
              <div className="flex flex-col items-center p-3 rounded-lg border border-gray-200 hover:border-nba-blue hover:shadow-md transition-all duration-200 bg-white min-w-[100px]">
                {/* Team Logo/Avatar */}
                <div className="w-12 h-12 bg-gradient-to-br from-nba-blue to-nba-red rounded-full flex items-center justify-center text-white text-sm font-bold mb-2 group-hover:scale-105 transition-transform duration-200">
                  {team.abbreviation || team.full_name?.substring(0, 3).toUpperCase() || 'NBA'}
                </div>

                {/* Team Name */}
                <div className="text-center">
                  <div className="text-sm font-medium text-gray-900 group-hover:text-nba-blue transition-colors leading-tight">
                    {team.city || team.full_name?.split(' ')[0] || 'Team'}
                  </div>
                  <div className="text-xs text-gray-500 leading-tight">
                    {team.nickname || team.full_name?.split(' ').slice(1).join(' ') || 'NBA'}
                  </div>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>

      {/* Scroll indicator */}
      <div className="mt-2 flex justify-center">
        <div className="text-xs text-gray-400 flex items-center">
          <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16l4-4m0 0l4 4m-4-4v12" />
          </svg>
          Scroll horizontally to see all teams
        </div>
      </div>
    </div>
  );
};

export default HorizontalTeamsBar;
