import React from 'react';

const NewsSection = ({ articles }) => {
  return (
    <div className="card p-6">
      <h2 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
        <span className="mr-2">ðŸ“°</span>
        Latest NBA News
      </h2>

      <div className="space-y-4">
        {articles.map((article, index) => (
          <article 
            key={index}
            className="p-4 border border-gray-200 rounded-lg hover:border-nba-blue hover:shadow-md transition-all duration-200 cursor-pointer"
          >
            <div className="flex justify-between items-start mb-2">
              <h3 className="font-semibold text-gray-900 hover:text-nba-blue transition-colors line-clamp-2">
                {article.title}
              </h3>
              <span className="text-xs text-gray-500 ml-4 flex-shrink-0">
                {article.source}
              </span>
            </div>

            <p className="text-gray-600 text-sm mb-3 line-clamp-2">
              {article.summary}
            </p>

            <div className="flex justify-between items-center text-xs text-gray-500">
              <span>
                {new Date(article.published_at).toLocaleDateString('en-US', {
                  month: 'short',
                  day: 'numeric',
                  hour: '2-digit',
                  minute: '2-digit'
                })}
              </span>
              <span className="text-nba-blue hover:text-nba-red transition-colors">
                Read more â†’
              </span>
            </div>
          </article>
        ))}
      </div>

      {articles.length === 0 && (
        <div className="text-center py-8 text-gray-500">
          No news articles available
        </div>
      )}

      <div className="mt-6 pt-4 border-t border-gray-200 text-center">
        <button className="text-nba-blue hover:text-nba-red font-medium transition-colors duration-200">
          Load More News
        </button>
      </div>
    </div>
  );
};

export default NewsSection;
