import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PredictionsWidget = () => {
  const [predictions, setPredictions] = useState(null);
  const [loading, setLoading] = useState(false);

  // Mock predictions for display
  const mockPredictions = [
    {
      player: "LeBron James",
      team: "Lakers",
      predictions: {
        points: 28.5,
        rebounds: 7.2,
        assists: 8.1,
        confidence: 0.89
      }
    },
    {
      player: "Stephen Curry",
      team: "Warriors", 
      predictions: {
        points: 31.2,
        rebounds: 4.8,
        assists: 6.5,
        confidence: 0.92
      }
    },
    {
      player: "Jayson Tatum",
      team: "Celtics",
      predictions: {
        points: 26.8,
        rebounds: 8.1,
        assists: 4.9,
        confidence: 0.85
      }
    }
  ];

  return (
    <div className="card p-6">
      <h2 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
        <span className="mr-2">ðŸ”®</span>
        ML Predictions
      </h2>

      <div className="space-y-4">
        {mockPredictions.map((pred, index) => (
          <div key={index} className="p-4 bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg border border-blue-200">
            <div className="flex justify-between items-start mb-3">
              <div>
                <h3 className="font-semibold text-gray-900">{pred.player}</h3>
                <p className="text-sm text-gray-600">{pred.team}</p>
              </div>
              <div className="text-right">
                <div className="text-xs text-gray-500">Confidence</div>
                <div className={`font-semibold ${
                  pred.predictions.confidence >= 0.9 ? 'text-green-600' :
                  pred.predictions.confidence >= 0.8 ? 'text-yellow-600' : 'text-red-600'
                }`}>
                  {(pred.predictions.confidence * 100).toFixed(0)}%
                </div>
              </div>
            </div>

            <div className="grid grid-cols-3 gap-2 text-center">
              <div className="bg-white rounded p-2">
                <div className="text-xs text-gray-500">PTS</div>
                <div className="font-bold text-nba-blue">{pred.predictions.points}</div>
              </div>
              <div className="bg-white rounded p-2">
                <div className="text-xs text-gray-500">REB</div>
                <div className="font-bold text-nba-blue">{pred.predictions.rebounds}</div>
              </div>
              <div className="bg-white rounded p-2">
                <div className="text-xs text-gray-500">AST</div>
                <div className="font-bold text-nba-blue">{pred.predictions.assists}</div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-6 pt-4 border-t border-gray-200">
        <button className="w-full btn-primary text-center">
          View All Predictions
        </button>
      </div>
    </div>
  );
};

export default PredictionsWidget;
